import React from 'react';

const ExperienciaProfissional = ({ data, updateData, errors }) => {
  const handleChange = (index, e) => {
    const { name, value } = e.target;
    const newExperiencias = [...data];
    newExperiencias[index] = { ...newExperiencias[index], [name]: value };
    updateData(newExperiencias);
  };

  const addExperiencia = () => {
    updateData([...data, { cargo: '', empresa: '', data_inicio: '', data_fim: '', descricao: '' }]);
  };

  return (
    <div>
      <h2>Experiência Profissional</h2>
      {data.map((exp, index) => (
        <div key={index}>
          <input
            type="text"
            name="cargo"
            value={exp.cargo}
            onChange={(e) => handleChange(index, e)}
            placeholder="Cargo"
          />
          <input
            type="text"
            name="empresa"
            value={exp.empresa}
            onChange={(e) => handleChange(index, e)}
            placeholder="Empresa"
          />
          <input
            type="date"
            name="data_inicio"
            value={exp.data_inicio}
            onChange={(e) => handleChange(index, e)}
          />
          <input
            type="date"
            name="data_fim"
            value={exp.data_fim}
            onChange={(e) => handleChange(index, e)}
          />
          <textarea
            name="descricao"
            value={exp.descricao}
            onChange={(e) => handleChange(index, e)}
            placeholder="Descrição das atividades"
          />
        </div>
      ))}
      {errors.experiencia && <span className="error">{errors.experiencia}</span>}
      <button type="button" onClick={addExperiencia}>Adicionar Experiência</button>
    </div>
  );
};

export default ExperienciaProfissional;

